/* Crea grid peso*altura del fondo
 * para el gráfico anterior
**********************************/
import org.apache.spark.sql.DataFrame

def createGrid() : DataFrame = {

    val maxTics = 255

    def fgrid (x:Int, min:Double, max:Double, tics: Int) : Double = {
        val tics2 = tics -2
        return ((x-1)*(max-min)+tics2*min)/tics2.toDouble
    }

    val (minKgs, maxKgs, minMts, maxMts) =
        people.agg(min('kgs), max('kgs), min('mts), max('mts))
            .as[(Double,Double,Double,Double)].first

    return sc.parallelize(0 to maxTics)
            .flatMap(x => (0 to maxTics).map((x,_)))
            .map{case (x,y) => (fgrid(x,minKgs,maxKgs,maxTics), fgrid(y,minMts,maxMts,maxTics))}
            .toDF("kgs","mts")
}

